INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Мистер WordPress', '', 'https://wordpress.org/', '', '2016-03-09 13:59:01', '2016-03-09 10:59:01', 'Привет! Это комментарий.\nЧтобы удалить его, авторизуйтесь и просмотрите комментарии к записи. Там будут ссылки для их изменения или удаления.', 0, '1', '', '', 0, 0),
(2, 58, 'Stephanie Kudrow', 'stephanie+simontomkins@gmail.com', 'http://www.commercegurus.com', '46.7.74.144', '2015-12-03 11:08:40', '2015-12-03 11:08:40', 'There is a concept that in hot climates, why not orientate buildings such that a large fan shape exists running north south, maximising the potential cooling normal to the axis most prominent to the exposure to solar gain, i.e., North and South Elevations?', 0, '1', '', '', 0, 0),
(3, 58, 'Simon Jennings', 'colmtroy@gmail.com', '', '46.7.74.144', '2015-12-03 11:11:08', '2015-12-03 11:11:08', 'Great idea Stephanie, that certainly would be something to look at.', 0, '1', '', '', 2, 0);
